var currentPage=1,perPage=5,btn=$("#btnload");function populate(e,t,a,l,i){var n=document.querySelector("#load"),r=document.createElement("li");r.classList.add("articleItem","articlePop","mt20","display-flex");var s=`\n        <a href="${a}" class="articleImg__wrap" aria-label="${t}">\n            <img src="${e}${i}.jpg" class="articleImg" alt="" width="178" height="155" loading="lazy" onerror="this.onerror=null;this.src='asset/images/default-thumbnail.jpg';">\n        </a>\n        <div class="articleBox">\n            <h3 class="articleTitle">\n                <a href="${a}" class="articleLink">${t}</a>\n            </h3>\n            <div class="articleLead">${l}</div>\n            <div class="articleDetail display-flex aitems-center mt10">\n                <div class="articleTime">Minggu, 18 Mei 2025</div>\n            </div>\n        </div>\n    `;r.innerHTML=s,n.appendChild(r)}function getResults(){$.getJSON("asset/json/results.json",(function(e){var t=e.posts,a=t.length,l=(currentPage-1)*perPage,i=Math.min(l+perPage,a);if(l>=a)btn.remove();else{for(let e=l;e<i;e++){let a=t[e];populate(a.img,a.title,a.link,a.lead,e)}i>=a&&btn.remove()}}))}getResults(),$((function(){btn.on("click",(function(){currentPage++,getResults()}))}));