function inc(e,a){let l=e.parentNode.querySelector('[name="number"]'),t=e.parentNode.querySelectorAll("button");l.value=parseInt(l.value)+1,t.forEach((e=>{e.classList.remove("disable")})),l.value>=parseInt(a)&&(l.value=a,e.classList.add("disable"))}function dec(e,a){let l=e.parentNode.querySelector('[name="number"]'),t=e.parentNode.querySelectorAll("button");l.value=parseInt(l.value)-1,t.forEach((e=>{e.classList.remove("disable")})),l.value<=parseInt(a)&&(l.value=a,e.classList.add("disable"))}