const appHeight=()=>{document.documentElement.style.setProperty("--app-height",`${window.innerHeight}px`)};function triggerPopup(e){document.querySelector(e).classList.add("active")}function closePopup(e){let t=document.querySelectorAll(".popup");if(e){document.querySelector(e).classList.remove("active")}else t.forEach((e=>{e.classList.remove("active")}))}window.addEventListener("resize",appHeight),appHeight();